import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

interface Comment {
  id: number;
  by: string;
  text: string;
  kids?: Comment[];
}

interface NewsItem {
  id: number;
  title: string;
  by: string;
  time: number;
  score: number;
  url: string;
  descendants: number;
  kids?: number[];
}

function Comments({ comment }: { comment: Comment }) {
  const [showReplies, setShowReplies] = useState(false);

  function handleToggleReplies() {
    setShowReplies(!showReplies);
  }

  if (!comment) return null;

  return (
    <div style={{ marginLeft: "1rem" }}>
      <div>{comment.by}</div>
      <div>{comment.text}</div>
      {comment.kids && (
        <button onClick={handleToggleReplies}>
          {showReplies ? "Hide replies" : "Show replies"}
        </button>
      )}
      {showReplies &&
        comment.kids?.map((kid) => (
          <Comments key={kid.id} comment={kid as Comment} />
        ))}
    </div>
  );
}

function NewsItem() {
  const { id } = useParams<{ id: string }>();
  const [news, setNews] = useState<NewsItem | null>(null);
  const [comments, setComments] = useState<Comment[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`https://hacker-news.firebaseio.com/v0/item/${id}.json`)
      .then((response) => response.json())
      .then((data: NewsItem) => {
        setNews(data);
        console.log(data);
        setLoading(false);
      });
  }, [id]);

  useEffect(() => {
    if (news?.kids) {
      Promise.all(
        news.kids.map((kid) =>
          fetch(`https://hacker-news.firebaseio.com/v0/item/${kid}.json`).then(
            (response) => response.json()
          )
        )
      ).then((data: Comment[]) => setComments(data));
    }
  }, [news]);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <main className="relative min-h-screen bg-white sm:flex sm:items-center sm:justify-center">
      <div className="relative sm:pb-16 sm:pt-8">
        <div className="mx-auto max-w-7xl sm:px-6 lg:px-8">
          <div className="relative shadow-xl sm:overflow-hidden sm:rounded-2xl">
            <div className="w-full rounded-lg border border-gray-200 bg-white p-4 shadow dark:border-gray-700 dark:bg-gray-800 sm:p-8">
              <div className="mb-4 flex items-center justify-between">
                <div className="flow-root">
                  <ul
                    role="list"
                    className="divide-y divide-gray-200 dark:divide-gray-700"
                  >
                    <li>
                      <div className="flex items-center space-x-4">
                        <div className="min-w-0 flex-1">
                         <p className="truncate text-sm font-medium text-gray-900 dark:text-white">
                            {news.title}
                            </p>

                          <div>
                            {comments.map((comment) => (
                              <Comments key={comment.id} comment={comment} />
                            ))}
                          </div>
                        </div>
                      </div>
                    </li>
                  </ul>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </main>
  );
}

export default NewsItem;
